import React, { useState } from "react";
import "./index.css";
import Seat from "../Seat";
import Button from "@mui/material/Button";
import seatsList from './SeatsList.json'

const BookingTickets = (props) => {
  const { movieDetails } = props;
  const { title } = movieDetails;
  const [seats, setSeats] = useState(seatsList);

  const updateSeatList = (seatId) => {
    setSeats((prevSeats) => {
      return prevSeats.map((seat) =>
        seat.id === seatId
          ? {
              ...seat,
              status: seat.status === "available" ? "booked" : "available",
            }
          : seat
      );
    });
  };
  console.log(seats)

  return (
    <div className="booking-container">
      <div className="movie-details">
        <h4 className="movie-name">{title}</h4>
        <p className="hall-name">Asian Cine World, Visakhapatnam</p>
      </div>
      <div>
        <ol className="seat-arrange">
          {seats.map((seat) => (
            <Seat key={seat.id} updateSeatList={updateSeatList} seat={seat} />
          ))}
        </ol>
      </div>
      <div className="flex-container-row">
        <div className="seat-available"></div>
        <p>Available</p>
        <div className="seat-solid"></div>
        <p>Solid</p>
        <div className="seat-selected"></div>
        <p>Selected</p>
      </div>
      <div className="button-container">
        <Button
          variant="contained"
          onClick={() => {
            console.log("hello");
          }}
        >
          Proceed
        </Button>
      </div>
    </div>
  );
};

export default BookingTickets;
