import React, { useState, useEffect } from "react";
import "./index.css";
import Seat from "../Seat";
import Button from "@mui/material/Button";
import axios from "axios";


const BookingTickets = (props) => {
  const { movieDetails, toogleButtonHandler } = props;
  const { title } = movieDetails;
  const [seats, setSeats] = useState([]);
  const [list,setList]=useState([])

  const updateListData=(id)=>{
    if (id in list){
      setList(old=>old!==id)
    }
    else{
      se
    }
  }

  console.log(list)

  const updateSeatList = (seatId) => {
    setSeats((prevSeats) => {
      return prevSeats.map((seat) =>
        seat.id === seatId
          ? {
              ...seat,
              status: seat.status === "available" ? "booked" : "available",
            }
          : seat
      );
    });
  };

  const menuButtonHandler = () => {
    toogleButtonHandler();
  };



  useEffect(() => {
    axios
      .get("http://localhost:3012/seats")
      .then((response) => {
        setSeats(response.data);
      })
      .catch((error) => {
        console.error("Error fetching seat data:", error);
      });
  }, []);

  const proceedHandler=()=>{
    setSeats((prevSeats) => {
      return prevSeats.map((seat)=>seat.status==='booked'?{...seat,staus:"sold"}:seat)
    });
  }


  return (
    <div className="booking-container">
      <div className="movie-details">
        <h4 className="movie-name">{title}</h4>
        <p className="hall-name">Asian Cine World, Visakhapatnam</p>
      </div>
      <div>
        <ol className="seat-arrange">
          {seats.map((seat) => (
            <Seat key={seat.id} updateSeatList={updateSeatList} updateListData={updateListData} seat={seat} />
          ))}
        </ol>
      </div>
      <div>
        <div className="screen-container"></div>
        <p className="para-text">All eyes this way please</p>
      </div>
      <div className="flex-container-row">
        <div className="seat-available"></div>
        <p>Available</p>
        <div className="seat-solid"></div>
        <p>Solid</p>
        <div className="seat-selected"></div>
        <p>Selected</p>
      </div>
      <div className="button-container">
        <Button variant="contained" onClick={menuButtonHandler}>
          Menu
        </Button>

        <Button
          variant="contained"
          onClick={proceedHandler}
        >
          Proceed
        </Button>
      </div>
    </div>
  );
};

export default BookingTickets;
