import React, { useEffect, useState } from "react";
import "./index.css";
import Seat from "../Seat";
import Button from "@mui/material/Button";
import axios from "axios";

const BookingTickets = (props) => {
  const { movieDetails } = props;
  const { title } = movieDetails;
  const [seats, setSeats] = useState([]);

  const updateSeatList = (seatId) => {
    setSeats((prevSeats) => {
      return prevSeats.map((seat) =>
        seat.id === seatId
          ? {
              ...seat,
              status: seat.status === "available" ? "booked" : "available",
            }
          : seat
      );
    });
  };
  useEffect(() => {
    // Fetch seat data from Firebase Realtime Database using Axios
    axios.get('YOUR_FIREBASE_REALTIME_DATABASE_URL/seats.json')
      .then(response => {
        // Handle the fetched data and set it to the local state
        const fetchedSeats = [];
        for (let key in response.data) {
          fetchedSeats.push({ id: key, ...response.data[key] });
        }
        setSeats(fetchedSeats);
      })
      .catch(error => {
        console.error('Error fetching seat data:', error);
      });
  }, []);

  return (
    <div className="booking-container">
      <div className="movie-details">
        <h4 className="movie-name">{title}</h4>
        <p className="hall-name">Asian Cine World, Visakhapatnam</p>
      </div>
      <div>
        <ol className="seat-arrange">
          {seats.map((seat) => (
            <Seat key={seat.id} updateSeatList={updateSeatList} seat={seat} />
          ))}
        </ol>
      </div>
      <div className="flex-container-row">
        <div className="seat-available"></div>
        <p>Available</p>
        <div className="seat-solid"></div>
        <p>Solid</p>
        <div className="seat-selected"></div>
        <p>Selected</p>
      </div>
      <div className="button-container">
        <Button
          variant="contained"
          onClick={() => {
            console.log("hello");
          }}
        >
          Proceed
        </Button>
      </div>
    </div>
  );
};

export default BookingTickets;
