import React, { useState } from "react";
import "./index.css";
import MovieList from "../MoviesList";
import BookingTickets from "../BookingTickets";
// import Footer from "./components/Footer";
// import Header from "./components/Header";


const HomePage = () => {
  const [bookingPage, setBookingPage] = useState(false);
  const [movieDetails,setMovieDetails]=useState(null)

  const toogleButtonHandler=()=>{
    setBookingPage(old=>!old)
  }
  const updateBooking=(movie)=>{
    setBookingPage(old=>!old)
    setMovieDetails(movie)
  }
  return <div>{bookingPage ? <BookingTickets movieDetails={movieDetails} toogleButtonHandler={toogleButtonHandler}  /> : <MovieList updateBooking={updateBooking}  />}</div>;
};

export default HomePage;
